import './App.css';

import { useState, useEffect } from 'react';

//4 - custom hook
import { useFetch} from './hooks/useFetch';

const url = "http://localhost:3000/products";

function App() {

	const [ products, setProducts ] = useState([]);

	//4 - custom hook
	const { data: items, httpConfig, loading, error } = useFetch(url);

	const [ id, setId ] = useState("");
	const [ name, setName ] = useState("");
	const [ price, setPrice ] = useState("");


	//1 - pegar dados
	// useEffect(() => {
	// 	async function fetchData() {		
	// 		const res =  await fetch(url);
	// 		const data = await res.json();
	// 		setProducts(data);		
	// 	}

	// 	fetchData();		
	//   }, []);


	// 2 - add de produtos
	const handleSubmit = async (e) => {
		e.preventDefault();

		document.querySelector("#botao").setAttribute("disabled", "disabled");

		const product = {
			name,
			price
		};

		// const res = await fetch(url, {
		// 	method: "POST",
		// 	headers:{
		// 		"Content-Type": "application/json"
		// 	},
		// 	body: JSON.stringify(product),
		// })

		// //3 - carregamento dinâmico
		// const addedProduct = await res.json();
		// setProducts((prevProducts) => [...prevProducts, addedProduct]);

		//5 - refatorando post
		httpConfig(product, "POST");
		document.querySelector("#botao").setAttribute("disabled", "");
		setName("");
		setPrice("");
	};


	const deleteSubmit = async (e) => {
		e.preventDefault();

		console.log(e.target)
		console.log(e.target.value)
	}


	return (
		<div className="App">
			<h1>Lista de Produtos</h1>
			{/* 6 - loading */}
			{loading && <p>Carregando dados...</p>}
			{error && <p>{ error }</p>}
			{!error && <ul>
				{items && items.map((product, key) => (
					<div>						
						<form onSubmit={deleteSubmit}>
							<li key={key}>{ product.name } - R$ {product.price}</li>	
							<button name="btn" >Remover</button>
							<input type="hidden" name="id" value={product.id} />
						</form>						
					</div>

				))}
			</ul>}
			<div className='add-product'>
				<form onSubmit={handleSubmit}>
					<label>
						Nome: 
						<input type="text" name="name" value={name} onChange={ (e) => setName(e.target.value) } />
					</label>
					<label>
						Preço: 
						<input type="text" name="preco" value={price} onChange={ (e) => setPrice(e.target.value) } />
					</label>
					{/* 7 - state de loading no post */}
					{loading && <input type="submit" disabled value="Aguarde" />}
					{!loading && <input id="botao" type="submit" value="Criar" />}					
				</form>
			</div>
		</div>
	);
}

export default App;
