import './App.css';

import { useState, useEffect } from 'react';

//4 - custom hook
import { useFetch, httpConfig, loading } from './hooks/useFetch';

const url = "http://localhost:3000/products";

function App() {

	const [ products, setProducts ] = useState([]);

	//4 - custom hook
	const { data: items, httpConfig, loading } = useFetch(url);

	const [ name, setName ] = useState("");
	const [ price, setPrice ] = useState("");


	//1 - pegar dados
	// useEffect(() => {
	// 	async function fetchData() {		
	// 		const res =  await fetch(url);
	// 		const data = await res.json();
	// 		setProducts(data);		
	// 	}

	// 	fetchData();		
	//   }, []);


	// 2 - add de produtos
	const handleSubmit = async (e) => {
		e.preventDefault();

		const product = {
			name,
			price
		};

		// const res = await fetch(url, {
		// 	method: "POST",
		// 	headers:{
		// 		"Content-Type": "application/json"
		// 	},
		// 	body: JSON.stringify(product),
		// })

		// //3 - carregamento dinâmico
		// const addedProduct = await res.json();
		// setProducts((prevProducts) => [...prevProducts, addedProduct]);

		//5 - refatorando post
		httpConfig(product, "POST");

		setName("");
		setPrice("");
	};


	return (
		<div className="App">
			<h1>Lista de Produtos</h1>
			{/* 6 - loading */}
			{loading && <p>Carregando dados...</p>}
			{!loading && <ul>
				{items && items.map((product, key) => (
					<li key={key}>{ product.name } - R$ {product.price}</li>
				))}
			</ul>}
			<div className='add-product'>
				<form onSubmit={handleSubmit}>
					<label>
						Nome: 
						<input type="text" name="name" value={name} onChange={ (e) => setName(e.target.value) } />
					</label>
					<label>
						Preço: 
						<input type="text" name="preco" value={price} onChange={ (e) => setPrice(e.target.value) } />
					</label>
					{/* 7 - state de loading no post */}
					{loading && <input type="submit" disabled value="Aguarde" />}
					{!loading && <input type="submit" value="Criar" onClick={(e) => { e.target.setAttribute("disabled", "disabled"); e.target.setAttribute("value", "NAO") } } />}
				</form>
			</div>
		</div>
	);
}

export default App;
