import './App.css';

import { useState, useEffect } from 'react';

const url = "http://localhost:3000/products";

function App() {

	const [ products, setProducts ] = useState([]);

	//1 - pegar dados
	useEffect( () =>  { 		
		const res =  fetch(url);
		const data = res.json();

		setProducts(data);		
	});

	//console.log(products);

	return (
		<div className="App">
			<h1>Lista de Produtos</h1>			
		</div>
	);
}

export default App;
